{% if T.union_types.size > 1 %}
      {% raise("Crystalizer::ByteFormat does not support unions; the protocol requires unambiguous field types.") %}
    {% end %}
deserializer = Deserializer::NonSelfDescribingObject.new(type)
deserializer.set_each_ivar do |variable|
  deserialize(variable.type)
end
deserializer.object_instance

{% if true %}
    {% i = 0 %}
      {
      {% for key, type in NT %}
        {{ key }}: (
          case value = @variables[{{ i }}]
          when nil      then {% if !type.nilable? %} raise Error.new "Missing {{ key }} value." {% end %}
          when {{ type }} then value
          else          raise Error.new "Incorrect type for {{ key }}: #{value}"
          end
        ),
      {% i += 1 %}
      {% end %}
      }
    {% end %}

{% if true %}
      {% i = 0 %}
      case key
      {% for key, type in NT %}
      when {{ key.stringify }}
        @variables[{{ i }}] = yield({{ type }}).as({{ type }})
        {% i += 1 %}
      {% end %}
      else raise Error.new "Missing key in {{ NT }}: #{key}"
      end
    {% end %}

{% for ivar in T.instance_vars %}
      {% ann = ivar.annotation(::Crystalizer::Field) %}
      {% if ann && ann[:ignore] %}{% else %}
        {% key = ((ann && ann[:key]) || ivar).id.stringify %}
        variable = Variable.new(
          type: {{ ivar.type }},
          annotations: {{ ann && ann.named_args }},
          nilable: false,
          has_default: {{ ivar.has_default_value? }}
        )
        pointerof(@object_instance.@{{ ivar }}).value = yield(variable).as {{ ivar.type }}
      {% end %}
    {% end %}
@ran = true

{% if true %}
    {% i = 0 %}
    case key
    {% for ivar in T.instance_vars %}
      {% ann = ivar.annotation(::Crystalizer::Field) %}
      {% if ann && ann[:ignore] %}{% else %}
        {% key = ((ann && ann[:key]) || ivar).id.stringify %}
        when {{ key }}
          raise Error.new "Duplicated field for {{ T }}: #{key}" if @found[{{ i }}]
          @found[{{ i }}] = true
          variable = Variable.new(
            type: {{ ivar.type }},
            annotations: {{ ann && ann.named_args }},
            nilable: {{ ivar.type.nilable? }},
            has_default: {{ ivar.has_default_value? }}
          )
          pointerof(@object_instance.@{{ ivar }}).value = yield(variable).as {{ ivar.type }}
        {% end %}
        {% i += 1 %}
      {% end %}
      else raise Error.new "Unknown field in {{ T }} matching the given string: #{key}"
      end
    {% end %}
